    font-size: 50%; //1rem = 8px = 8/16 = 50%
  }
  @include respond(phone) { //width<= 600px
    font-size: 30%; //1rem = 4.8px = 4.8/16 = 30%
  }
  @include respond(big-desktop) {
    font-size: 75%; //1rem = 12px = 12/16 = 75%
  }
}
body {
  box-sizing: border-box;
  padding: 3rem;
  @include respond(tap-port){
      padding :0;
  }
}

